import React, { Component, Fragment } from 'react';
import RandomMenu from './component/RamdomMenu';
import "./css/app.css";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      selectRestorant: null,
      info: [
        {
          id: 0,
          name: "어부사시가",
          food: "생선구이"
        },
        {
          id: 1,
          name: "킷사서울",
          food: "오므라이스"
        },
        {
          id: 2,
          name: "버거킹",
          food: "햄버거"
        }
      ]
    }
  }
  createMenu = (dataList) => {
    console.log(dataList);

  }
  selectMenu = (dataList) => {
    const randValue = Math.floor(Math.random() * this.state.info.length);
    console.log(randValue);
    this.setState({ selectRestorant: this.state.info });
    console.log(this.state.selectRestorant);
  }

  render() {
    const { info } = this.state;
    return (
      <Fragment>

        <div className="spoon"></div>
        <div className="fork"></div>

        <div className="App-main">
          <h1>오늘의 점심은 ?</h1>
          <p>버튼을 눌러 보세요.</p>
          <RandomMenu onCreateMenu={this.createMenu} dataList={info} onSelectMenu={this.selectMenu} />
        </div>
        <div id="popup">
          <div id="popmenu">
            <p>{info.filter(num => num === 0)}</p>
            <div className="exit">닫기</div>
          </div>
        </div>
      </Fragment>

    );
  }
}

export default App;
