import React, { Component } from 'react';

class RandomMenu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            select_restorant: ""
        }
    }
    Submit = (e) => {
        // 페이지 리로딩 방지
        e.preventDefault();
        // 상태값을 onCreate 를 통하여 부모에게 전달
        this.props.onCreateMenu(this.state);

    }
    Random = (e) => {
        // 페이지 리로딩 방지
        e.preventDefault();
        this.props.randData()
    }
    selectMenu = (dataList) => {
        const randValue = Math.floor(Math.random() * this.state.info.length);
        this.setState({ select_restorant: this.props.dataList.indexOf(randValue) });
    }
    render() {
        return (
            <form onSubmit={this.Submit}>
                <button onClick={this.selectMenu}></button>

            </form>
        );
    }
}

export default RandomMenu;
