import React, { Component, Fragment } from 'react';

class RandomMenu extends Component {
    constructor(props) {
        super(props);
        this.state = {
            select_restorant: ""
        }
    }
    Submit = (e) => {
        // 페이지 리로딩 방지
        e.preventDefault();
        // 상태값을 onCreate 를 통하여 부모에게 전달
        this.props.onCreateMenu(this.state);

    }

    selectMenu = (dataList) => {
        const randValue = Math.floor(Math.random() * this.props.dataList.length);
        console.log(randValue);
        this.setState({ select_restorant: this.props.dataList });
        console.log(this.state.select_restorant);
    }
    render() {
        return (
            <Fragment>
                <form onSubmit={this.Submit}>
                    <button id="contents" onClick={this.selectMenu}></button>

                </form>
                <div id="popup">
                    <div id="popmenu">
                        <p>안녕하세요!</p>
                        <div className="exit">닫기</div>
                    </div>
                </div>
            </Fragment>
        );
    }
}

export default RandomMenu;
