import React, { Component, Fragment } from 'react';
import RandomMenu from './component/RamdomMenu';



class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      food: '',
      img: '',
      url: '',
      info: [
        { id: 0, name: "어부사시가", food: "#생선구이 #찌개", img: "images/fish.jpeg", url: "https://store.naver.com/restaurants/detail?entry=plt&id=1039016287&query=%EC%96%B4%EB%B6%80%EC%82%AC%EC%8B%9C%EA%B0%80" },
        { id: 1, name: "킷사서울", food: "#오므라이스 #덮밥", img: "images/egg.jpeg", url: "https://store.naver.com/restaurants/detail?entry=plt&id=1218049409&query=%ED%82%B7%EC%82%AC%EC%84%9C%EC%9A%B8" },
        { id: 2, name: "버거킹", food: "#몬스터 와퍼 #콰트로 치즈", img: "images/bugger.jpg", url: "https://store.naver.com/restaurants/detail?id=1318392263" },
        { id: 3, name: "안녕부산", food: "#돼지 국밥 #밀면", img: "images/busan.jpeg", url: "https://store.naver.com/restaurants/detail?entry=plt&id=1862909287&query=%EC%95%88%EB%85%95%EB%B6%80%EC%82%B0" },
        { id: 4, name: "새마을식당", food: "#김치찌개 #고기", img: "images/saema.jpeg", url: "https://store.naver.com/restaurants/detail?id=13374695" },
        { id: 5, name: "김가네", food: "#찌개 #분식 #여러가지 음식", img: "images/kimga.jpeg", url: "https://store.naver.com/restaurants/detail?id=37274681" },
        { id: 6, name: "놀부 부대찌개", food: "#부대찌개", img: "images/nolboo.jpeg", url: "https://store.naver.com/restaurants/detail?id=11803440" },
        { id: 7, name: "유가네 닭갈비", food: "#숯불 닭갈비 #닭갈비 볶음밥", img: "images/ugan.jpeg", url: "https://store.naver.com/restaurants/detail?id=20717283" },
        { id: 8, name: "삼백돈", food: "#돈까스 #스윙스", img: "images/samback.jpeg", url: "https://store.naver.com/restaurants/detail?id=648063703" }
      ]
    }
  }
  createMenu = (dataList) => {
    console.log(dataList);

  }
  selectMenu = (dataList) => {
    const randValue = Math.floor(Math.random() * this.state.info.length);
    console.log(randValue);
    this.setState({
      name: this.state.info.filter(num => num.id === randValue).map(v => v.name),
      food: this.state.info.filter(num => num.id === randValue).map(v => v.food),
      img: this.state.info.filter(num => num.id === randValue).map(v => v.img),
      url: this.state.info.filter(num => num.id === randValue).map(v => v.url)
    });
  }

  render() {
    const { info } = this.state;
    const { name, food, img, url } = this.state;

    return (
      <Fragment>

        <div className="spoon"></div>
        <div className="fork"></div>

        <div className="App-main">
          <h1>오늘의 점심은 ?</h1>
          <p>버튼을 눌러 보세요.</p>
          <RandomMenu onCreateMenu={this.createMenu} dataList={info} onSelectMenu={this.selectMenu} />
        </div>
        <div id="popup">
          <h1>오늘의..점심은..바로!!</h1>
          <div id="popmenu">
            <h1>{name}  <span>{food}</span></h1>
            <img src={img} alt="식당 이미지" />
            <p>＊ 본 이미지는 실제 매장 음식입니다.</p>
            <a target='_blank' href={url}>가게 정보 보러가기 </a>
            <div className="exit">닫기</div>
          </div>
        </div>
      </Fragment>

    );
  }
}

export default App;
