import React, { Component, Fragment } from 'react';
import RandomMenu from './component/RamdomMenu';
import "./css/app.css";

function fadeIn(el) {
  var opacity = 0;

  el.style.opacity = 0;
  el.style.filter = '';

  var last = +new Date();
  var tick = function () {
    opacity += (new Date() - last) / 400;
    el.style.opacity = opacity;
    el.style.filter = 'alpha(opacity=' + (100 * opacity) | 0 + ')';

    last = +new Date();

    if (opacity < 1) {
      (window.requestAnimationFrame && requestAnimationFrame(tick)) || setTimeout(tick, 16);
    }
  };

  tick();
}
function fadeOut(el) {
  var opacity = 1;

  el.style.opacity = 1;
  el.style.filter = '';

  var last = +new Date();
  var tick = function () {
    opacity -= (new Date() - last) / 400;
    el.style.opacity = opacity;
    el.style.filter = 'alpha(opacity=' + (100 * opacity) | 1 + ')';

    last = +new Date();

    if (opacity > 0) {
      (window.requestAnimationFrame && requestAnimationFrame(tick)) || setTimeout(tick, 16);
    }
  };

  tick();
}
var content = document.getElementById("contents");
var popup = document.getElementById("popup");
document.addEventListener("DOMContentLoaded", function () {
  content.addEventListener("click", function () {
    fadeIn(popup);
  });
  popup.addEventListener("click", function () {
    fadeOut(popup);
  });
});

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      info: [
        {
          id: 0,
          name: "어부사시가",
          food: "생선구이"
        },
        {
          id: 1,
          name: "킷사서울",
          food: "오므라이스"
        },
        {
          id: 2,
          name: "버거킹",
          food: "햄버거"
        }
      ]
    }
  }
  createMenu = (dataList) => {
    console.log(dataList);
  }

  render() {
    const { info } = this.state;
    return (
      <Fragment>

        <div className="spoon"></div>
        <div className="fork"></div>

        <div className="App-main">
          <h1>오늘의 점심은 ?</h1>
          <p>버튼을 눌러 보세요.</p>
          <RandomMenu onCreateMenu={this.createMenu} dataList={info} />
        </div>
        <div id="popup">
          <div id="popmenu">
            <p>안녕하세요!</p>
            <div class="exit">닫기</div>
          </div>
        </div>
      </Fragment>

    );
  }
}

export default App;
